subplot1 = subplot(1,3,1);
hold(subplot1,'on');

% Create plot
plot(X1,Y2,"LineWidth",1.2);

% Create ylabel
ylabel('$C_L$ [-]');

% Create xlabel
xlabel('Angle of attack ($\alpha$)');



