subplot3 = subplot(1,3,3);
hold(subplot3,'on');

% Create plot
plot(X1,Y2./Y1,"LineWidth",1.2);

% Create ylabel
ylabel('Aerodynamic Efficiency $C_L/C_D$ [-]');

% Create xlabel
xlabel('Angle of attack ($\alpha$)');


box(subplot3,'on');
grid(subplot3,'on');
hold(subplot3,'off');


% Create title
sgtitle("Aircraft Aerodynamic Coefficients Models vs Angle of Attack");
